create table user (id integer generated by default as identity (start with 1, increment by 1) primary key, name varchar(255), username varchar(255), email varchar(255), phone varchar(21), website varchar(255), address_id integer, company_id integer);
create table address (id integer generated by default as identity (start with 1, increment by 1) primary key, street varchar(255), suite varchar(255), city varchar(255), zipcode varchar(20), geo_id integer);
create table company (id integer generated by default as identity (start with 1, increment by 1) primary key, name varchar(255), catchphrase varchar(255), bs varchar(255));
create table geo (id integer generated by default as identity (start with 1, increment by 1) primary key, lat varchar(20), lng varchar(20));
create table post (user_id integer, id integer generated by default as identity(start with 1, increment by 1) primary key, title varchar(255), body varchar(255), FOREIGN KEY (user_id) REFERENCES user(id));
create table comment (post_id integer, id integer generated by default as identity(start with 1, increment by 1) primary key, name varchar(255), email varchar(255), body varchar(500), FOREIGN KEY (post_id) REFERENCES post(id));